"use strict";window.onload=function(){document.addEventListener("click",function(e){e.target.classList.contains("hamburger")&&(document.getElementById("site-navigation").classList.add("main-menu--mobile"),document.getElementById("primary-menu").classList.add("primary-menu--mobile")),e.target.classList.contains("hamburger-close")&&(document.getElementById("site-navigation").classList.remove("main-menu--mobile"),document.getElementById("primary-menu").classList.remove("primary-menu--mobile"))})};